from django.db import models
from django.core.files.storage import FileSystemStorage
from datetime import datetime, timedelta, date
from django.contrib.auth.models import AbstractUser



from phonenumber_field.modelfields import PhoneNumberField



# ### storage location
# fs = FileSystemStorage(location='media/photos')
# avatar = models.ImageField(storage=fs, null=True, blank=True)


class User(AbstractUser):
    #first_name last_name username is definied in AbstractUser, email as well but it's been overrided here to make it unique 
    dob = models.DateField(null=True, blank=True)
    gender = models.CharField(max_length=1,choices= [('m','male'),('f','female')])
    registerDate = models.DateField(auto_now_add=True)
    avatar = models.TextField(default="data:image/png;base64,")
    country = models.CharField(max_length=30, null=True, blank=True)
    email = models.EmailField(unique=True, null=False, blank=False)
    password = models.CharField(max_length=1024)
    phone = PhoneNumberField(null=True, blank=True )
    #to get string number use User.phone.as_e164 
    currentShow = models.ForeignKey(to='Show', null=True, blank=True, on_delete=models.SET_NULL)
    showsHistory = models.ManyToManyField(to='Show', related_name='viewers', related_query_name='viewer', through='ShowHistory', through_fields=('user','show'), blank=True)
    showsplaylist = models.ManyToManyField(to='Show', related_name='fans', related_query_name='fan',through='ShowPlaylist', through_fields=('user','show'), blank=True)
    membership = models.ForeignKey(to='Membership', null=True, blank=True, on_delete=models.SET_NULL)
    membershipStartDate = models.DateField(null=True, blank=True)
    isVerified = models.BooleanField(default=False)
    isPaid = models.BooleanField(default=False)

    def isMembershipEnded(self):
        if self.membership and self.membershipStartDate:
            return  date.today() > self.membershipStartDate + timedelta(days=self.membership.duration) 
        else:
            return True
            
    def age(self):
        if self.dob:
            return int(datetime.now().strftime('%Y')) - int(self.dob.strftime('%Y')) 
        

    def __str__(self):
        return f"id:{self.pk}, username:{self.username}, email:{self.email}"

